import React from 'react';
import styled from 'styled-components/';

export const StyledDiv = styled.div`
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    text-align: center;
`
export const StyledDiv2 = styled.div`
    display: flex;
    flex-wrap: wrap;
    width: 30%;
    justify-content: center;
    text-align: center;
    border: 1px solid black;
`
